mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num,writer,title,content,viewCount,regdate FROM QNA_FREE_QUESTION
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE QNA_FREE_QUESTION
SET viewCount=viewCount+1
WHERE num=#{num}

UPDATE QNA_FREE_QUESTION
SET title=#{title}, content='${content}'
WHERE num=#{num}
