mapper.select:
SELECT id, pwd, profile, email, regdate, phone, TO_CHAR(birth,'YYYY-MM-DD') birth
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET email=#{email }, profile=#{profile, jdbcType=VARCHAR}, phone=#{phone}, birth=#{birth}
WHERE id=#{id }

UPDATE users
SET email=#{email } , profile=#{profile }, phone=#{phone}, birth=#{birth}
WHERE id=#{id }

UPDATE users
SET pwd=#{newPwd}
WHERE id=#{id}
